import React, { Component } from 'react';
import './Counter.css';

class Counter extends Component{

    constructor(){
        super(); //common Error
        this.state={
            counter : 0
        }

        this.increment = this.increment.bind(this);
    }

    render() {
        return (
          <div className="Counter">
           <CounterButton by={1} incrementMethod={this.increment}/>
           <CounterButton by={5} incrementMethod={this.increment}/>
           <CounterButton by={10} incrementMethod={this.increment}/>
           <CounterButton by={1} decrementMethod={this.decrement}/>
           <CounterButton by={5} decrementMethod={this.decrement}/>
           <CounterButton by={10} decrementMethod={this.decrement}/>
           <span className="count">{this.state.counter}</span>
          </div>
        );
      }

    increment(by) { //update state - counter ++
        this.setState(
            (prevState) => {
                return {counter : prevState.counter + by}
            }
        );
    }

    decrement(by){
        this.setState(
            (prevState) => {
                return {counter : prevState.counter - by}
            }
        );

    }



}

class CounterButton extends Component {

    //Define the intial state in the constructor 
    //state  => counter 0

    constructor(){
        super(); //common Error
        this.state={
            counter : 0
        }

        this.increment = this.increment.bind(this);
    }

    render(){
        return(
            <div className = "CounterButton" >
                <button onClick={this.increment} >+{this.props.by}</button>
                {/* <span className="count">{this.state.counter}</span> */}
            </div>
        );
    }

    increment() { //update state - counter ++
        //console.log('increment');
        //this.state.counter++; //Bad Practice
        this.setState({
            counter : this.state.counter + this.props.by 
        });

        this.props.incrementMethod(this.props.by);
    }

    decrement(){
        this.props.decrementMethod(this.props.by);
    }
}

export default Counter;
